export default function FrameIcon({
  className,
  id,
  from
}: {
  className?: string;
  id: string;
  from: string;
}) {
  return from === "list" ? (
    <FrameIconSmall className={className} id={id} />
  ) : (
    <FrameIconBig className={className} />
  );
}

